<template>
  <view style="widht: 100%">
    <view v-if="show == 1" class="loading-wapper">
      <view class="loading-icon"></view>
      <text class="loading-txt">{{ i18n['加载中'] }}</text>
    </view>
    <view v-if="show == 2">
      <slot></slot>
    </view>
  </view>
</template>
<script>
export default {
  name: 'benben-empty',
  options: {
    virtualHost: true,
  },
  props: {
    listData: {
      type: Array,
      default() {
        return []
      },
    },
  },
  watch: {
    'listData.length': {
      handler(value) {
        console.log(value)
        if (value == 0) {
          this.show = 1
          this.setTimeoutFn = setTimeout(() => {
            this.show = 2
          }, 300)
        } else {
          this.setTimeoutFn && clearTimeout(this.setTimeoutFn)
          this.show = 3
        }
      },
      immediate: true,
    },
  },
  data() {
    return {
      show: 1,
      setTimeoutFn: null,
    }
  },
}
</script>

